import React, { Component } from "react";

class Searchbar extends Component {
  state = {
    search: "",
    type: "all",
  };

  handleText = (e) => {
    if (e.key === "Enter") this.props.searchMovies(this.state.search);
  };

  handleCategory = (e) => {
    this.setState({ type: e.target.dataset.type });
    this.props.searchMovies(
      this.state.search,
      e.target.getAttribute("datatype")
    );
  };

  render() {
    const { search } = this.state;
    return (
      <div className="search">
        <div className="search-bar">
          <input
            type="search"
            name="search"
            required
            value={search}
            autoComplete="off"
            onChange={(e) => this.setState({ search: e.target.value })}
            onKeyUp={this.handleText}
          />
          <button className="search-btn" type="submit">
            <span>Search</span>
          </button>
        </div>
        <div className="radio-toggles">
          <input
            type="radio"
            name="radio-options"
            id="option-1"
            datatype="movie"
            onChange={this.handleCategory}
            checked={this.t}
          />
          <label htmlFor="option-1">Movies</label>
          <input
            className="radio-toggle"
            type="radio"
            name="radio-options"
            id="option-2"
            datatype="all"
            onChange={this.handleCategory}
            checked={this.t}
          />
          <label htmlFor="option-2">All</label>
          <input
            type="radio"
            name="radio-options"
            id="option-3"
            datatype="series"
            onChange={this.handleCategory}
            checked={this.t}
          />
          <label htmlFor="option-3">Series</label>
          <div className="slide-item"></div>
        </div>
      </div>
    );
  }
}

export default Searchbar;
